document.getElementById("submit").addEventListener("click", function(event) {
    event.preventDefault();
    checkData();
});
var fullname = document.getElementById("floatingInput");

function checkData() {
    var fullnamevalue = fullname.value;
    if (fullnamevalue == "") {
        setError(fullname, "Full name cannot be empty");
    } else {
        setSuccess(fullname, "//Hint Text");
    }
}

function setError(f, msg) {
    var parentBox = f.parentElement;
    parentBox.className = "form-floating error";
    var span = parentBox.querySelector("span");
    var fa = parentBox.querySelector(".fa");
    span.innerText = msg;
    fa.className = "fa-duotone fa-circle-xmark";
}

function setSuccess(f, msg) {
    var parentBox = f.parentElement;
    parentBox.className = "form-floating success";
    var span = parentBox.querySelector("span");
    span.innerText = msg;
    var fa = parentBox.querySelector(".fa");
    fa.className = "fa-duotone fa-circle-xmark";
}
